class ToTop extends HTMLElement{connectedCallback(){this.innerHTML+='\n      <a href="#" aria-label="Scroll to top" title="Scroll to top">\n        <svg width="45px" height="45px" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">\n          <path d="M6 15L12 9L18 15" stroke="#fff" stroke-width="1" stroke-linecap="round" stroke-linejoin="round"/>\n        </svg>\n      </a>\n      '}}customElements.define("to-top",ToTop);const toTop=document.querySelector("body").appendChild(document.createElement("to-top"));window.addEventListener("scroll",(()=>{window.pageYOffset>100?toTop.classList.add("active"):toTop.classList.remove("active")}));
